// src/comps/ParticleBackground/ParticleBackground.js

import React, { useEffect } from 'react';

// Declare particlesJS as a global variable (eslint-disable-next-line to ignore ESLint warning)
/* global particlesJS */

const ParticleBackground = () => {
  useEffect(() => {
    // Initialize particles.js
    particlesJS("particles-js", {
      particles: {
        number: { value: 150, density: { enable: true, value_area: 1600 } },
        color: { value: "#ffffff" },
        shape: { type: "circle", stroke: { width: 0, color: "#000000" }, polygon: { nb_sides: 5 } },
        opacity: { value: 0.5, random: false, anim: { enable: false, speed: 0.2, opacity_min: 0.1, sync: false } },
        size: { value: 2, random: true, anim: { enable: false, speed: 10, size_min: 0.1, sync: false } },
        line_linked: { enable: true, distance: 200, color: "#ffffff", opacity: 0.4, width: 1 },
        move: { enable: true, speed: 3, direction: "none", random: false, straight: false, out_mode: "out", bounce: false, attract: { enable: false, rotateX: 600, rotateY: 1200 } }
      },
      interactivity: {
        detect_on: "canvas",
        events: {
          onhover: { enable: true, mode: "repulse" },
          onclick: { enable: true, mode: "push" },
          resize: true
        },
        modes: {
          grab: { distance: 400, line_linked: { opacity: 1 } },
          bubble: { distance: 400, size: 40, duration: 2, opacity: 8, speed: 3 },
          repulse: { distance: 200, duration: 0.4 },
          push: { particles_nb: 4 },
          remove: { particles_nb: 2 }
        }
      },
      retina_detect: true
    });

    // Clean up function
    return () => {
      window.pJSDom = []; // Remove any existing particle instances
    };
  }, []);

  return <div id="particles-js" className="particle-background"></div>;
};

export default ParticleBackground;
